<?phpclass CadastroController extends Zend_Controller_Action {    public function indexAction() {        $ModelClientes = new Admin_Model_Clientes();        if ($this->_request->isPost()) {            $data = $this->_request->getPost();            if(empty($data['nome1'])){                 $this->_helper->FlashMessenger->addMessage(array('erro' => 'Insira o 1º nome'));                $this->_redirect("/cadastro");            }                        if(empty($data['nome2'])){                 $this->_helper->FlashMessenger->addMessage(array('erro' => 'Insira o 2º nome'));                $this->_redirect("/cadastro");            }                        if(empty($data['doc1'])){                 $this->_helper->FlashMessenger->addMessage(array('erro' => 'Insira o 1º Documento'));                $this->_redirect("/cadastro");            }                         if(empty($data['doc2'])){                 $this->_helper->FlashMessenger->addMessage(array('erro' => 'Insira o 2º Documento'));                $this->_redirect("/cadastro");            }                         if(empty($data['cep'])){                 $this->_helper->FlashMessenger->addMessage(array('erro' => 'Insira o CEP'));                $this->_redirect("/cadastro");            }                         if(empty($data['cidade'])){                 $this->_helper->FlashMessenger->addMessage(array('erro' => 'Insira a cidade'));                $this->_redirect("/cadastro");            }                         if(empty($data['estado'])){                 $this->_helper->FlashMessenger->addMessage(array('erro' => 'Insira o estado'));                $this->_redirect("/cadastro");            }                         if(empty($data['telefone'])){                 $this->_helper->FlashMessenger->addMessage(array('erro' => 'Insira o telefone'));                $this->_redirect("/cadastro");            }                         if(empty($data['email'])){                 $this->_helper->FlashMessenger->addMessage(array('erro' => 'Insira o E-mail'));                $this->_redirect("/cadastro");            }                         if(empty($data['senha'])){                 $this->_helper->FlashMessenger->addMessage(array('erro' => 'Insira a senha'));                $this->_redirect("/cadastro");            }                         if($data['senha']!==$data['confirma_senha']){                $this->_helper->FlashMessenger->addMessage(array('erro' => 'As senhas não se coincide!'));                $this->_redirect("/cadastro");            }            if(!$data['tipo_pessoa']){                $data['tipo_pessoa'] = 1;            }                        //Zend_Debug::dump($data);die;            $ModelClientes->insert($data);            $this->_helper->FlashMessenger->addMessage(array('sucesso' => 'cadastro realizado com sucesso!'));            $this->_redirect("/cadastro");        }    }}